
const events = require("events");
const emitter = new events.EventEmitter();

async function AutoKill({Client, Time}) {
if(!Client) {
    throw new Error('Provide the Client!');
}
if(!Time) {
    console.error(`Auto Kill : [ No Time Provided, Defaulting to 40 Seconds!]`)
}
if(Time < 5000) {
    throw new Error('Time Must Be More Than 5 Seconds')
}
let Time2 = Time || 40000;
let MohamedAutoKillClient = Client;

setInterval(async () => {
if(!MohamedAutoKillClient.user || !MohamedAutoKillClient) {
    await process.kill(1);
    await console.log('Process Killed, Relogging!')
} else {
return
}
}, Time2)
}


